                    <td>{element.email}</td>
                    <td>{element.password}</td>
                    <td>{element.contact}</td>
                    <td>{element.qualification}</td>
                    <td>{element.city}</td>
                    <td>{element.area}</td>
                    <td>   <Link to="/tutiondetails/:id" className="btn btn-primary" style={{ width: "60%" }} onClick={() => this.gettutionid(element.tution_id)} >Details</Link> </td>
