import React, {Component} from "react"
import {Link} from "react-router-dom"
import Modal from "react-modal"
import Modal3 from "react-bootstrap/Modal"
import Button from "react-bootstrap/Button"




class DashBoard extends Component{
    state={
        loading: false,
        tution: [],
        isActive: false
    }   

    componentDidMount(){
        this.setState({loading: true})
        fetch("http://localhost:4000/dashboard")
            .then(response => response.json()) 
            .then(data => { 
                this.setState({
                    loading: false,
                    tution : data
                })
                console.log(data)
            })
    }

    componentWillMount(){
        Modal.setAppElement('body');
    }

    showmodal = () => {
        this.setState({
        isActive : !this.state.isActive
        })
    }

    
    
    
    render(){
       const Styling={
       top:"100px",
        left:"-50px"

       }
       
       return(
        <div>
            
            
            
            
            <div>

                <ul>{this.state.tution.map(element => {
        
                return(
                    <li key={element.tution_id} >{element.tution_id} {element.studentname} {element.grade} {element.fee} {element.timing} {element.area} {element.city} {element.endtiming}</li>
               
                )
                })}
                </ul>
                </div>
    
                
                <Button onClick={this.showmodal} style={Styling}>open modal</Button>

                {/* Modal Part */}
           <Modal isOpen={this.state.isActive} 
                onRequestClose = {this.state.isActive}>

                        <Modal3.Dialog>
                        <Modal3.Header>
                            
                            <Modal3.Title>Request Demo</Modal3.Title>
                         </Modal3.Header>

                        <Modal3.Body>
                             <p>Do you want to request demo for this tution</p>
                        </Modal3.Body>

                        <Modal3.Footer>
                            <Button variant="danger" onClick={this.showmodal}>Cancel</Button>
                            <Button variant="success" onClick={this.showmodal}>Request Demp</Button>
                        </Modal3.Footer>
                        </Modal3.Dialog>
           </Modal>    

                <body>
                <div class="btn-group">
  <button class="btn btn-secondary btn-sm dropdown-toggle" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
    Small button
  </button>
  <div class="dropdown-menu">
  <a class="dropdown-item" href="#">Action</a>
    <a class="dropdown-item" href="#">Another action</a>
    <a class="dropdown-item" href="#">Something else here</a>
    <div class="dropdown-divider"></div>
    <a class="dropdown-item" href="#">Separated link</a>
  </div>
  </div>
                </body>
	
         </div>
        )
    }
}


export default DashBoard
