import React, { Component } from "react"
import NavBar from "./navbar3.js"
import {Button} from "react-bootstrap"
import {Link} from "react-router-dom"

class tutiondisplay extends Component{
    
    constructor(){
        super()
        this.state={
            tution1:[],
            tution2:[]
        }
    }

    async componentDidMount() {
        console.log("1st api")
            
      await  fetch(`http://localhost:4000/tutiondisplay1`, {
            method: 'get',
            headers: new Headers({ 'Content-Type': 'applications/json', 'authorization': localStorage.getItem('tok') })
        })
            .then(response => response.json())
            .then(data => {
                this.setState({
                    tution1: data

                })

            })

            await  fetch(`http://localhost:4000/tutiondisplay2`, {
            method: 'get',
            headers: new Headers({ 'Content-Type': 'applications/json', 'authorization': localStorage.getItem('tok') })
        })
            .then(response => response.json())
            .then(data => {
                this.setState({
                    tution2: data

                })

            })



        if (localStorage.getItem('tok') !== 'undefined' &&
            localStorage.getItem('tok') &&
            localStorage.getItem('type') == 'finder') {

        }
        else {
            this.props.history.push('/')
        }
    }

    
    render(){
        return(
            <div>
                <NavBar />
               <Link to='/tutionform'><Button className="btn-success" style={{fontSize:15,fontWeight:"bold",position:"absolute",right:150,width:130,height:40}}><span className="glyphicon glyphicon-plus"></span>NEW TUTION</Button></Link>
            <div style={{fontSize:12,liststyle:"none"}}>
            {this.state.tution1.map(arr=>{
                return(
                <li key={arr.tution_id}>Your post about tution with id: <span style={{fontWeight:"bold"}}>{arr.tution_id}</span> and student name: <span style={{fontWeight:"bold"}}>{arr.studentname}</span> has been approved and can be displayed to the tutors</li>
                )
            })}

            <br/>

            {this.state.tution2.map(arr=>{
                return(
                <li key={arr.tution_id}>Your post about tution with id: <span style={{fontWeight:"bold"}}>{arr.tution_id}</span> and student name: <span style={{fontWeight:"bold"}}>{arr.studentname}</span> is waiting for approval from the admin</li>
                )
            })}
            </div>
            </div>
        )
    }
}

export default tutiondisplay
