import React,{Component} from "react"
import Modal from "react-modal"
import Modal3 from "react-bootstrap/Modal"
import Button from "react-bootstrap/Button"
import "bootstrap/dist/css/bootstrap.min.css"


class Modal1 extends Component{
    
    constructor(){
        super();
        this.state = {
            isActive: false
        }
    }

    componentWillMount(){
        Modal.setAppElement('body');
    }

    showmodal = () => {
        this.setState({
        isActive : !this.state.isActive
        })
    }

    
    render(){
        return(
            <div>

    

<button id="myBtn" onClick = {this.showmodal}>Open Modal</button>



<Modal isOpen={this.state.isActive} 
                onRequestClose = {this.state.isActive}>
<div id="myModal" className="modal">

  <div className="modalContent">
  	<div className="modalHeader d-flex justify-content-between align-items-center">
	    <span id="closebtn"><i className="fas fa-times" /></span>
	    
  	</div>
    <div className="modalBody text-center">Do you want to request for demo?</div>
    <hr/>
    <div className="modalFooter d-flex justify-content-end align-items-center">
	    <button type="button" className="modalButton" onClick={this.showmodal}>Request Demo</button>
	</div>
  </div>
 </div>
 
 </Modal>
            </div>
        )
            
        

        }
}


class modal2 extends Component{
    constructor(){
        super();
        this.state={
            isActive: false
        }
    }
    
    componentWillMount(){
        Modal.setAppElement('body');
    }

    showModal = () => {
        this.setState({
        isActive : !this.state.isActive
        })
    }


    render(){
        return(
            <div>
                <Button onClick={this.showModal}>open modal</Button>
                
                <Modal isOpen={this.state.isActive} 
                onRequestClose = {this.state.isActive}>


                        <Modal3.Dialog>
                        <Modal3.Header closeButton >
                            <Modal3.Title>Modal title</Modal3.Title>
                         </Modal3.Header>

                        <Modal3.Body>
                             <p>Modal body text goes here.</p>
                        </Modal3.Body>

                        <Modal3.Footer>
                            <Button variant="secondary" onClick={this.showModal}>Close</Button>
                            <Button variant="primary" onClick={this.showModal}>Save changes</Button>
                        </Modal3.Footer>
                        </Modal3.Dialog>
                
                </Modal>

            </div>

        )
    }
}


export default modal2
