import React, { Component } from "react"
import Modal from "react-modal"
import Modal3 from "react-bootstrap/Modal"
import Button from "react-bootstrap/Button"
import "bootstrap/dist/css/bootstrap.min.css"





class modal2 extends Component {
    constructor() {
        super();
        this.state = {
            isActive: false,
            contact:[]
        }
    }

    componentWillMount() {
        Modal.setAppElement('body');
    }

    componentDidMount(){
        fetch(`http://localhost:4000/contact/${this.props.contactdetails}`,
        {method:'GET',
        headers: new Headers({'Content-Type':'application/json', 'authorization':localStorage.getItem('tok')})
    })

        .then(response => response.json())
        .then(data=>{
            this.setState({
                contact:data
            })
        })
    }

    showModal = () => {
        this.setState({
            isActive: !this.state.isActive
        })
    }


    render() {
        return (
            <div>
                <Button onClick={this.showModal}>open modal</Button>

                <Modal isOpen={this.state.isActive}
                    onRequestClose={this.state.isActive}>


                    <Modal3.Dialog>
                        <Modal3.Header closeButton >
                            <Modal3.Title>Modal title</Modal3.Title>
                        </Modal3.Header>

                        <Modal3.Body>
                            console.log(this.state.contact)
                            <h6>Contact: {this.state.contact}</h6>
                        </Modal3.Body>

                        <Modal3.Footer>
                            <Button variant="secondary" onClick={this.showModal}>Close</Button>
                            
                        </Modal3.Footer>
                    </Modal3.Dialog>

                </Modal>

            </div>

        )
    }
}


export default modal2
