import React, {Component} from "react"
import {Link} from "react-router-dom"
import Modal from "react-modal"

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
  };




class DashBoard extends Component{
    state={
        loading: false,
        tution: [],
        isActive: false
    }   

    componentDidMount(){
        this.setState({loading: true})
        fetch("http://localhost:4000/dashboard")
            .then(response => response.json()) 
            .then(data => { 
                this.setState({
                    loading: false,
                    tution : data
                })
                console.log(data)
            })
    }

    componentWillMount(){
        Modal.setAppElement('body');
    }

    showmodal = () => {
        this.setState({
        isActive : !this.state.isActive
        })
    }

    
    
    render(){
       
        return(
        <div>  
            <div>

                <ul>{this.state.tution.map(element => {
        
                return(
                    <li key={element.tution_id} >{element.tution_id} {element.studentname} {element.grade} {element.fee} {element.timing} {element.area} {element.city} {element.endtiming}</li>
               
                )
                })}
                </ul>
                </div>
    <body>

               
           <section>
               <div className ="modal_open button">         
           <button onClick = {this.showmodal}>show modal</button>
                </div>
            </section>

            
                    {/* modal div */}
                    
                    <meta charset="UTF-8" />
 
  <meta name="viewport" content="width=device-width, initial-scale=1"/>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/normalize/5.0.0/normalize.min.css"/>
    <link rel='stylesheet' href='https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.9.0/css/all.min.css'/>
    <link rel='stylesheet' href='https://cdnjs.cloudflare.com/ajax/libs/normalize/8.0.1/normalize.min.css'/>
    <link rel="stylesheet" href="modals/style.css"/>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/prefixfree/1.0.7/prefixfree.min.js"></script>
            
           <div>
                <Modal 
                isOpen={this.state.isActive} 
                onRequestClose = {this.state.isActive}
                
                >
                     <div className="modal">
		                <div className="modal_close main">
			                <i className="fas fa-times"/>
		                </div>
            
                        <div className="copy">
			                <p>do you want to request demo for this tution?</p> 
            
                            <button className="button" onClick={this.showmodal}>Request</button>
            
                        </div> 
                    
                    </div>
                    
                    <div class="modal_overlay"></div>
                </Modal>
            </div>
    </body>
           
    <script src='https://cdnjs.cloudflare.com/ajax/libs/jquery/3.4.1/jquery.min.js'></script>
	<script  src="public/modals/script.js"></script>
         </div>
        )
    }
}


export default DashBoard
