import React, {Component} from "react"

class LoginForm extends Component{
    
    constructor(){
        super();
        this.state = {
            email: "",
            password:""
        }
        this.changehandler = this.changehandler.bind(this)
        this.submithandler = this.submithandler.bind(this)
    }

    submithandler(){
        fetch('http://localhost:4000/login',{
            method: 'post',
            headers: {'Content-Type': 'application/json', 'accept': "application/json"},
            body: JSON.stringify({
                "email":this.state.email,
                "password":this.state.password
                
            })
        })
    }

    changehandler = name => event => {
        this.setState({
            [name]: event.target.value
        })
        }
    
    
    
    render(){
        return(
                <div>
                    <div>
                        <input type= "email" value= {this.state.email} onChange = {this.changehandler("email")} />
                    </div>

                    <div>
                        <input type = "password" value= {this.state.password} onChange = {this.changehandler("password")}/>
                    </div>

                    <div>
                        <button onClick = {this.submithandler}>login</button>
                    </div>
                </div>
        )
    }
}



export default LoginForm
