import React, {Component} from "react"

class Template extends Component{
    
    constructor(){
        super()
        this.state = {
            fullname: "",
            email: "",
            password:"",
            qualification: "",
            area:"",
            contact:""
        }
        this.changehandler = this.changehandler.bind(this);
        this.submithandler = this.submithandler.bind(this);
    }


    submithandler(){
        fetch('http://localhost:4000/signup',{
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                "fullname":this.state.fullname,
                "email":this.state.email,
                "password":this.state.password,
                "qualification":this.state.qualification,
                "area":this.state.area,
                "contact": this.state.contact})
        })
    }

    changehandler(event){
        const{name, value, checked, type}=event.target
        event.target.type==="checkbox" ? this.setState({[event.target.name] : event.target.checked}) : this.setState({
            [event.target.name]: event.target.value
        })
        }
    
    
    
    render(){

        
        return(
            <div>
                <form>
                    <div>
                        <input type = "text" name="fullname" value={this.state.fullname} onChange = {this.setchangeHandler} />
                    </div>
                    <div>
                        <input type = "email" name="email" value={this.state.email} onChange = {this.setchangeHandler}/>
                    </div>
                    <div>
                        <input type = "password" name="password" value={this.state.password} onChange = {this.setchangeHandler}/>
                    </div>
                    <div>
                        <input type = "text" name="qualification" value={this.state.qualification} onChange = {this.setchangeHandler}/>
                    </div>
                    <div>
                        <input type = "text" name="area" value={this.state.area} onChange = {this.setchangeHandler}/>
                    </div>
                    <div>
                        <input type = "tel" name="contact" value={this.state.contact} onChange = {this.setchangeHandler}/>
                    </div>
                </form>
            </div>
        )
    }
}

export default Template
