import React, {Component} from "react"
import {Link} from "react-router-dom"
import Modal from "react-modal"
import Modal3 from "react-bootstrap/Modal"
import {Button,Dropdown,Table} from "react-bootstrap"
import "bootstrap/dist/css/bootstrap.min.css"
import { List } from "react-bootstrap"




class DashBoard extends Component{
    state={
        loading: false,
        tution: [],
        isActive: false
    }   

    componentDidMount(){
        this.setState({loading: true})
        fetch("http://localhost:4000/dashboard")
            .then(response => response.json()) 
            .then(data => { 
                this.setState({
                    loading: false,
                    tution : data
                })
                console.log(data)
            })
    }

    componentWillMount(){
        Modal.setAppElement('body');
    }

    showmodal = () => {
        this.setState({
        isActive : !this.state.isActive
        })
    }

    
    
    
    render(){
       const Styling={
       top:"100px",
        left:"-50px"
          
       }
       var abc="something"
       
       return(
        <div>
            <div class="container">
            
            
            <Table striped bordered hover variant="dark">
  
                            <thead>
                              <tr>
                                <th>#</th>
                                <th>First Name</th>
                              </tr>
                            </thead>
                          

                
                <tr>{this.state.tution.map(element => {
            
                return(
                    <div>
                    <tbody>
                    <td key={element.tution_id}> {element.tution_id} </td>
                    {element.studentname}
                <td>{element.city}</td>
                    </tbody>
                   </div>
                    )

                })}
                </tr>
               
                </Table>
                
               </div>
                
                
                <Button onClick={this.showmodal} style={Styling}>open modal</Button>

                {/* Modal Part */}
           <Modal isOpen={this.state.isActive} 
                onRequestClose = {this.state.isActive}>

                        <Modal3.Dialog>
                        <Modal3.Header>
                            
                            <Modal3.Title>Request Demo</Modal3.Title>
                         </Modal3.Header>

                        <Modal3.Body>
                             <p>Do you want to request demo for this tution</p>
                        </Modal3.Body>

                        <Modal3.Footer>
                            <Button variant="danger" onClick={this.showmodal}>Cancel</Button>
                            <Button variant="success" onClick={this.showmodal}>Request Demp</Button>
                        </Modal3.Footer>
                        </Modal3.Dialog>
           </Modal>    

                <body>
                
                {/* drop down button */}
                
                <Dropdown>
  <Dropdown.Toggle variant="success" id="dropdown-basic">
    Dropdown Button
  </Dropdown.Toggle>

  <Dropdown.Menu>
    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
  </Dropdown.Menu>
</Dropdown>
                </body>

                    
         </div>
        )
    }
}


export default DashBoard
