import React,{Component} from "react"
import { waitForElement } from "@testing-library/react"
import NavBar from "./navbar.js"
import Axios from "axios"
import Footer from "./footer.js"
import {Redirect, Link} from "react-router-dom"
import history from "./history.js"
class Profile extends Component{
    
    state={
        user:[],
        profilenumber: 11,
    }




    componentDidMount(){
            fetch(`http://localhost:4000/profile/${this.state.profilenumber}`)
            .then(response => response.json())
            .then(data=>{
                this.setState({
                    user: data
                })
                console.log(data)
            })
    }

    
    
    
    render(){
        return(
            
            <div>
                
                <NavBar/>
                 
                    <ul style={{listStyleType:"none"}}>
                    {this.state.user.map(element => {

                        return(
                           <div> 
                        <li key={element.user_id} style={{position:"relative",left:"50px",fontSize:"15px"}}>Name {element.fullname}</li> 
                        <li > Contact {element.contact}</li>
                        </div>
                        )
                    })}
                    </ul>

                    <Link to="/editprofile" className="btn btn-primary" style={{position:"relative" , left:"50px"}}>edit Profile</Link>
                    <Footer />
            </div>
        )
    }
}


export default Profile
