import React, { Component } from "react"
import Footer from "./footer.js"
import NavBar from "./navbar.js"
import { Link, Redirect } from "react-router-dom"
import { Button, Dropdown, Table } from "react-bootstrap"
class TutionDetails extends Component {

    constructor() {
        super()
        this.state = {
            tution: []


        }

        this.submithandler = this.submithandler.bind(this);
    }


    componentDidMount() {
        fetch(`http://localhost:4000/dashboard`, {
            method: 'get',
            headers: new Headers({ 'Content-Type': 'applications/json', 'authorization': localStorage.getItem('tok') })
        })

            .then(response => response.json())
            .then(data => {
                this.setState({
                    tution: data

                })

            })

        if (localStorage.getItem('tok') !== 'undefined' && localStorage.getItem('tok')) {

        }
        else {
            this.props.history.push('/')
        }
    }

    async submithandler(id) {

        await fetch('http://localhost:4000/addbooking', {
            method: 'post',
            headers: { 'Content-Type': 'application/json', 'accept': "application/json", 'authorization': localStorage.getItem('tok') },
            body: JSON.stringify({
                "tution_id": id

            })


        }).then(res => res.json())
            .then(data => {
                this.setState({
                    dem: data.demo
                })
            })


    }

    render() {
        return (
            <div>
                <NavBar />
                <div className="container" style={{ width: 800, height: 320, border: "1px solid black", backgroundColor: "#9889F2" }}>
                    <h3>Tution Details</h3>
                    <hr style={{ backgroundColor: "#514F62" }} />
                    <ul>
                        
                        {this.state.tution.map(element => {

                            return (

                                    <ul style={{fontFamily:"verdana",fontSize:15, listStyle:"none"}}>
                                        <br />                                
                                        <li key={element.tution_id}></li>
                                        <hr />
                                        <li style={{MozTabSize:4}}>Student Name: {element.studentname}</li>
                                        <li>Grade:                          {element.grade}</li>
                                        <li>Fee:                            {element.fee}</li>
                                        <li>Area:                           {element.area}</li>

                                    </ul>

                            )

                        })}
                    </ul>


                    <Button variant="danger" style={{ float: "right" }}>Cancel</Button>
                    <Button variant="success" style={{ float: "right" }}>Request Demo</Button>
                </div>
                <Footer />

            </div>
        )
    }
}

export default TutionDetails
