import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Template from './template.js'
import LoginForm from './loginform.js'
class App extends Component {

  constructor(){
    super();
    this.state={
      buttonID : ""
    }
    this.setbutton = this.setbutton.bind(this)
  }

  setbutton(id){
    this.setState({buttonID: id});
  }

  render(){
  return (
    <div>
      <button onClick={() => this.setbutton(1)}>login</button>
      <button onClick={() => this.setbutton(2)}>signup</button>
      {this.state.buttonID === 1 && <LoginForm/>}
      {this.state.buttonID === 2 && <Template />}


    </div>
  );

}
}

export default App;
