import React, {Component} from "react"

class Template extends Component{
    
    constructor(){
        super()
        this.state = {
            fullname: "",
            email: "",
            password:"",
            qualification: "",
            area:"",
            contact:""
        }
        this.changehandler = this.changehandler.bind(this);
        this.submithandler = this.submithandler.bind(this);
    }


    submithandler(){
        fetch('http://localhost:4000/signup',{
            method: 'post',
            headers: {'Content-Type': 'application/json', 'accept': "application/json"},
            body: JSON.stringify({
                "fullname":this.state.fullname,
                "email":this.state.email,
                "password":this.state.password,
                "qualification":this.state.qualification,
                "area":this.state.area,
                "contact": this.state.contact})
        })
    }

    changehandler = name => event => {
        this.setState({
            [name]: event.target.value
        })
        }
    
    
    
    render(){

        
        return(
            <div>
                <form>
                    <div>
                        <input type = "text"  placeholder="yourname" value={this.state.Fullname} onChange = {this.changehandler("fullname")} />
                    </div>
                    <div>
                        <input type = "email"  placeholder="youremail" value={this.state.email} onChange = {this.changehandler("email")}/>
                    </div>
                    <div>
                        <input type = "password"  placeholder="yourpassword" value={this.state.password} onChange = {this.changehandler("password")}/>
                    </div>
                    <div>
                        <input type = "text"  placeholder="yourqualification" value={this.state.qualification} onChange = {this.changehandler("qualification")}/>
                    </div>
                    <div>
                        <input type = "text" placeholder="yourarea" value={this.state.area} onChange = {this.changehandler("area")}/>
                    </div>
                    <div>
                        <input type = "tel" placeholder="yourcontact" value={this.state.contact} onChange = {this.changehandler("contact")}/>
                    </div>
                    <button onClick = {this.submithandler}>submit</button>
                </form>
            </div>
        )
    }
}

export default Template
