import React, { Component } from "react"
import { Link } from "react-router-dom"
import Modal from "react-modal"
import Modal3 from "react-bootstrap/Modal"
import { Button, Dropdown, Table } from "react-bootstrap"
import "bootstrap/dist/css/bootstrap.min.css"
import { List } from "react-bootstrap"
import NavBar from "./navbar.js"
import Profile from "./profile.js"
import axios from "axios"
import Footer from "./footer.js"


class DashBoard extends Component {
    state = {
        
        tution: [],
        isActive: false
    }

    componentDidMount(){
        fetch(`http://localhost:4000/dashboard` , {
            method:'get',
            headers: new Headers({'Content-Type':'applications/json','authorization':localStorage.getItem('tok')})
        })
        
        .then(response => response.json())
        .then(data=>{
            this.setState({
                tution: data
            })
            
        })

        if(localStorage.getItem('tok')!=='undefined' && localStorage.getItem('tok')){
            
        }
            else{
                this.props.history.push('/')
            }
}

    componentWillMount() {
        Modal.setAppElement('body');
    }

    showmodal = () => {
        this.setState({
            isActive: !this.state.isActive
        })
    }

    async submithandler(id){
		
        await fetch('http://localhost:4000/addbooking',{
            method: 'post',
            headers: {'Content-Type': 'application/json', 'accept': "application/json",'authorization':localStorage.getItem('tok')},
            body: JSON.stringify({
                "tution_id": id
                
			})
			
			
        })
    

}



    render() {



        return (


            <div>
                <NavBar/>
                <body>

                    {/* drop down button */}

                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            Dropdown Button
  </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                            <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                            <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </body>


                <br />


                <div class="container">


                    <Table striped bordered hover variant="dark">

                        <thead>
                            <tr>
                                <th>#</th>
                                <th>student Name</th>
                                <th>grade</th>
                                <th>fee</th>
                                <th>area</th>
                                <th></th>
                            </tr>
                        </thead>



                        {this.state.tution.map(element => {

                            return (
                                
                                    <tbody>
                                        <tr>
                                            <td key={element.tution_id}> {element.tution_id} </td>
                                            <td>{element.studentname}</td>
                                            <td>{element.grade}</td>
                                            <td>{element.fee}</td>
                                            <td>{element.area}</td>
                                            <td>    <Button 
                                                onClick={() => {
                                                    this.submithandler(element.tution_id);
                                                    
                                                    }} >Request Demo</Button> </td>
                                        </tr>

                                    </tbody>
                                
                            )

                        })}

                    </Table>
                </div>





                {/* Modal Part */}
                <Modal isOpen={this.state.isActive}
                    onRequestClose={this.state.isActive}>

                    <Modal3.Dialog>
                        <Modal3.Header>

                            <Modal3.Title>Request Demo</Modal3.Title>
                        </Modal3.Header>

                        <Modal3.Body>
                            <p>Do you want to request demo for this tution</p>
                        </Modal3.Body>

                        <Modal3.Footer>
                            <Button variant="danger" onClick={this.showmodal}>Cancel</Button>
                            <Button variant="success" onClick={this.showmodal}>Request Demo</Button>
                        </Modal3.Footer>
                    </Modal3.Dialog>
                </Modal>

                        

                       

                <Footer />
            </div>
        )
    }
}


export default DashBoard
