<thead>
    <tr>
        <th>Studentname</th>
        <th>Fee</th>
        <th>Grade</th>
        <th>Area</th>
        
        
        <th></th>
    </tr>
</thead>



{this.state.details.map(element => {

    return (

        <tbody style={{ fontSize: 12 }}>
