import React, { Component } from "react"
import NavBar from "./navbar.js"
import Footer from "./footer.js"
import {Redirect, Link} from "react-router-dom"
class editProfile extends Component {
    
    constructor(){
        super()
        this.state = {
            fullname: "",
            email: "",
            password:""
            
        }
        this.changehandler = this.changehandler.bind(this);
        this.submithandler = this.submithandler.bind(this);
    }

    async submithandler(event){
		event.preventDefault();
        await fetch('http://localhost:4000/editprofile',{
            method: 'put',
            headers: {'Content-Type': 'application/json', 'accept': "application/json"},
            body: JSON.stringify({
                "fullname":this.state.fullname,
                "email":this.state.email,
				"password":this.state.password,
			})
			
			
        }).then(res=>res.json()).then((res=>localStorage.setItem('tok',res.token)))
    
if(localStorage.getItem('tok')!=='undefined' && localStorage.getItem('tok')){
	this.props.history.push('/editprofile')
}
	else{
		this.setState({staus:"Failed"})
	}
}

changehandler = name => event => {
    this.setState({
        [name]: event.target.value
    })
    }
    
    
    
    
    render() {
       
        return (
            <div>
                <NavBar />
                <div class="container">
    
  	
    <div class="row">
      
      <div class="col-md-9 personal-info">
        
        <h3>Personal info</h3>
        
        <form class="form-horizontal" role="form">
          <div class="form-group">
            <label class="col-lg-3 control-label">Full name:</label>
            <div class="col-lg-8">
              <input class="form-control" type="text"  value={this.state.fullname} onChange={this.changehandler("fullname")} />
            </div>
          </div>
          
          <div class="form-group">
            <label class="col-lg-3 control-label">Email:</label>
            <div class="col-lg-8">
              <input class="form-control"  type="text" value={this.state.email} onChange={this.changehandler("email")} />
            </div>
          </div>
          
         
          <div class="form-group">
            <label class="col-md-3 control-label">Password:</label>
            <div class="col-md-8">
              <input class="form-control" type="password"  value={this.state.password} onChange={this.changehandler("password")} />
            </div>
          </div>
          
          <div class="form-group">
            <label class="col-md-3 control-label"></label>
            <div class="col-md-8">
              <input type="button" class="btn btn-primary" value="Save Changes" onClick = {this.submithandler} />
              <span></span>
              <Link to ="/profile"  class="btn btn-default" value="Cancel"> </Link>
            </div>
          </div>
        </form>
      </div>
  </div>
</div>

                <Footer/>
            </div>
            
        )
    }
}




export default editProfile
