import React, {Component} from "react"
import {Link} from "react-router-dom"
import Modal from "react-modal"

class DashBoard extends Component{
    state={
        loading: false,
        tution: [],
        isActive: false
    }   

    componentDidMount(){
        this.setState({loading: true})
        fetch("http://localhost:4000/dashboard")
            .then(response => response.json()) 
            .then(data => { 
                this.setState({
                    loading: false,
                    tution : data
                })
                console.log(data)
            })
    }

    componentWillMount(){
        Modal.setAppElement('body');
    }

    showmodal = () => {
        this.setState({
        isActive : !this.state.isActive
        })
    }

    
    
    render(){
       
        return(
        <div>  
            <div>

                <ul>{this.state.tution.map(element => {
        
                return(
                    <li key={element.tution_id} >{element.tution_id} {element.studentname} {element.grade} {element.fee} {element.timing} {element.area} {element.city} {element.endtiming}</li>
               
                )
                })}
                </ul>
                </div>
           
           
           <button onClick = {this.showmodal}>show modal</button>
        
                    {/* modal div */}
        
           <div>
                <Modal 
                isOpen={this.state.isActive} 
                onRequestClose = {this.state.isActive}
                style = {this.state.customstyles}
                >
                    
                    hello world
                    <button onClick={this.showmodal}>close modal</button>
                </Modal>
            </div>
         </div>
        )
    }
}


export default DashBoard
